data.script:
let scripts = this.api.selectWidgets("t9wx9bui3oo").$refs.instance.scripts
let getScript = name => _.find(scripts,item => item.name == name).script

this.api.axios.get("https://raw.githubusercontent.com/boldak/SQL-exercise/master/SQL_exercise_01/questions.txt").then( res => {
    let problems = res.data.split("\n").filter(r => r).map((r,index) =>{
        let parts = r.split("#")
        return {
            problem: parts[0],
            query: parts[1],
            index: index+1
        }
    })
})

let widgets = {
    editor: this.api.selectWidgets( w => w.config.name == "editor"),
    problem: this.api.selectWidgets( w => w.config.name == "problem"),
    result: this.api.selectWidgets( w => w.config.name == "result"),
    solution: this.api.selectWidgets( w => w.config.name == "solution"),

    t1: this.api.selectWidgets( w => w.config.name == "t1"),
    t2: this.api.selectWidgets( w => w.config.name == "t2"),
}

let loading = {
    data: '<div class="primary--text title mx-2 my-5"><center><i class="mdi mdi-spin mdi-select"></i></center></div>'
}

widgets.t1.update(loading)
widgets.t2.update(loading)


let problems = [];
let problemIndex = 0




let updateProblem = () => {
    widgets.problem.update({data:'<div class="primary--text title mx-2 my-5"><center><i class="mdi mdi-spin mdi-help-circle-outline"></i></center></div>'})
    widgets.solution.update({data:'<div class="secondary--text body-1 mx-2 my-5"><center><i class="mdi mdi-spin mdi-select"></i></center></div>'})

    this.api.runDps(getScript("PREPARE_PROBLEM"), problems[problemIndex])
        .then( res => {
            widgets.problem.update(res)
        })
    this.api.runDps(getScript("EXECUTE_SOLUTION"), problems[problemIndex])
        .then( res => {
            if(res.type=="error"){
                this.api.runDps(getScript("PREPARE_ERROR"),{message: res.data.message})
                    .then( res => {
                        widgets.solution.update(res)
                    })
            } else {
                widgets.solution.update(res)    
            }
        })
}


this.api.axios.get("https://raw.githubusercontent.com/boldak/SQL-exercise/master/SQL_exercise_01/questions.txt").then( res => {
    problems = res.data.split("\n").filter(r => r).map((r,index) =>{
        let parts = r.split("#")
        return {
            problem: parts[0],
            query: parts[1],
            index: index+1
        }
    })
    updateProblem()
})



this.off()
this.on({
    event:"run-sql-query",
    callback: (query) => {
        widgets.result.update({data:'<div class="secondary--text body-1 mx-2 my-5"><center><i class="mdi mdi-spin mdi-select"></i></center></div>'})

        this.api.runDps(getScript("EXECUTE_QUERY"), {query})
        .then( res => {
            if(res.type=="error"){
                console.log(res)
                this.api.runDps(getScript("PREPARE_ERROR"),{message: res.data.message})
                    .then( res => {
                        widgets.result.update(res)
                    })
            } else {
                widgets.result.update(res)    
            }
        })
    }
})

this.on({
    event:"next-problem",
    callback: () => {
        problemIndex++
        problemIndex = (problemIndex == problems.length) ? 0 : problemIndex
        updateProblem()
    }
})

this.on({
    event:"prev-problem",
    callback: () => {
        problemIndex--
        problemIndex = (problemIndex<0) ? problems.length-1 : problemIndex
        updateProblem()
    }
})

