data.script:
let infoWidget = this.api.selectWidgets("8wmxxt6y2p6")
let log = []
let info = message => {

    log.unshift(message)
    if(log.length > 5) log.pop()

    infoWidget.update({
        data:`<div style="padding:1em;
    font-size:12px;
    border: 1px solid #cfd7e6;
    box-shadow: 0 1px 3px 0 rgba(89,105,129,.05), 0 1px 1px 0 rgba(0,0,0,.025);
    min-height:2em;">
    ${log.join("<br/>")}
</div>`
    })
}

this.off()

this.on({
    event:"select-entity",
    callback: (selection) => {
        info(`${selection.length} items selected`)
    }
})

this.on({
    event:"create-entity",
    callback: (entity) => {
        info(`"${entity.childs.map(node => node.value).join('')}" annotated as "${entity.type}"`)
    }
})

this.on({
    event:"remove-entity",
    callback: (entity) => {
        info(`Remove annotation "${entity.type}"`)
    }
})

let original = this.api.selectWidgets("hyme90ga287")
let current = this.api.selectWidgets("kuf176uapa")
let diff = this.api.selectWidgets("u03jojga7v")
let decoration = original.getInstance().getDecoration()
let utils = diff.getInstance().getUtils()

let originalDoc = original.getInstance().getDocument()
originalDoc.branch = "initial"
let currentDoc = current.getInstance().getDocument()
currentDoc.branch = "annotator"

diff.update({
    data: {
        decoration,
        document: utils.getDifferenceTom(originalDoc,current.getInstance().getDocument())
    }
})

// this.off()

this.on({
    event:"change-doc",
    callback: (doc, sender) => {
        // console.log("Change", doc, sender.config.id, current.config.id)
            if(sender.config.id == current.config.id){
                currentDoc = doc       
            }
            if(sender.config.id == original.config.id){
                originalDoc = doc
            }
            diff.update({
                data: {
                    decoration,
                    document: utils.getDifferenceTom(originalDoc,currentDoc)
                }
            })

    }
})

this.on({
    event:"override",
    callback: () => {
        // console.log("OVERRIDE")
        originalDoc = utils.getDifferenceTom(currentDoc,currentDoc)
        originalDoc.branch = "initial"
        original.update({
            data:{
                decoration,
                events: current.getInstance().getEvents(),
                document: originalDoc
            }
        })
    }
})

let master = this.api.selectWidgets("ynw37c8yjc")
let dev = this.api.selectWidgets("f6395e0zyef")
let merge = this.api.selectWidgets("uaavmxlt5w8")
let resolve = this.api.selectWidgets("z7wfpvsy8p")

let currentStrategy = "EQUAL"

let masterData = null
let devData = null
let mergeData = null 
let resolveData = [
	{
		"title": "resolve",
		"event": "resolve",
		"color": "warning",
		"disabled": true
	}
]

this.$nextTick(() => {

    masterData = master.getInstance().getData()
    devData = dev.getInstance().getData()
    devData.document = utils.getDifferenceTom(masterData.document,masterData.document)

    mergeData = merge.getInstance().getData() 
    mergeData.document = utils.getDifferenceTom(masterData.document,masterData.document)
    masterData.document.branch = "master"
    devData.document.branch = "dev"

    dev.update({data:devData})
    merge.update({data:mergeData})
})

this.on({
    event:"change-dev",
    callback: (doc, sender) => {
            mergeData.document = utils.mergeTom(utils.getDifferenceTom(masterData.document,devData.document), utils.resolveStrategies[currentStrategy]) 
            merge.update({ data: mergeData })
    }
})

this.on({
    event:"change-master",
    callback: (doc, sender) => {
            mergeData.document = utils.mergeTom(utils.getDifferenceTom(masterData.document,devData.document), utils.resolveStrategies[currentStrategy]) 
            merge.update({ data: mergeData })
    }
})

this.on({
    event:"change-merge",
    callback: (doc, sender) => {
        // console.log("Change", doc, sender.config.id)
        // console.log(!!utils.findNode(doc, node => node.type == "DIFF"))
        this.$nextTick(() => {
            resolveData[0].disabled = !!utils.findNode(doc, node => node.type == "DIFF")
            resolve.update({data: resolveData})
        })
    }
})

this.on({
    event:"resolve",
    callback: (doc, sender) => {
        // console.log("Change", doc, sender.config.id)
        // console.log(!!utils.findNode(doc, node => node.type == "DIFF"))
        masterData.document = utils.getDifferenceTom(mergeData.document,mergeData.document)
        masterData.document.branch = "master"
        master.update({data: masterData})

    }
})


this.on({
    event:"select-strategy",
    callback: ( sender, data ) => {
        currentStrategy = data.selection.filter( s => s.selected)[0].entity.id
        mergeData.document = utils.mergeTom(utils.getDifferenceTom(masterData.document,devData.document), utils.resolveStrategies[currentStrategy]) 
        merge.update({ data: mergeData })
    }
})

