/**
 **
 **     DATA PROCESSING SCRIPT: pulse
 **     SERVICE: http://localhost:8098
 **
 **/

get("data")

c.group(by:<? 
    item => item.publishedAt
?>)

c.map(<? 
    item => ({
        date: new Date(item.group),
        value: item.values.length
    })
?>)
c.order(by:"date")
set("data")

<?javascript
    $scope.timeline = []
    let t;
    $scope.data.forEach( item => {
        if(!t){
            t = item.date
            $scope.timeline.push(item)
        } else {
            while( !moment(t).isSame(moment(item.date),'hour')) {
                t = moment(t).add(1,"h")
                $scope.timeline.push({
                    date:t.toDate(),
                    value:0
                })
            }
            t = item.date
            $scope.timeline[$scope.timeline.length-1].value = item.value
        }
    })

?>

