let mediator = this;
let scripts = mediator.api.selectWidgets("ap94qxuxdwv").$refs.instance.scripts
let getScript = name => _.find(scripts,item => item.name == name).script

let html = mediator.api.selectWidgets("t971u6hd2i")
let pulse = mediator.api.selectWidgets("tgvjl4ye6gp")
let sources = mediator.api.selectWidgets("outhaprynr")
let title = mediator.api.selectWidgets("eyhhkkqw60s")
let geo = mediator.api.selectWidgets("zs48b1o5smo")
let warn = mediator.api.selectWidgets("2w1oto9ze46")

warn.hide()
html.hide()
pulse.hide()
sources.hide()
title.hide()
geo.hide()


this.on({
	event: "keywords-select", 
	callback: (emitter, keywords) => {

        html.hide()
        pulse.hide()
        sources.hide()
        title.hide()
        geo.hide()
        warn.hide()

        if(keywords.length > 0){
            let p = mediator.api.progress({text:"get news", maxStage:5})
            mediator.$nextTick(() => {
                mediator.api.runDps(getScript("google news"),{keywords})
                .then( response => {
                    // console.log("gn", response)
                    if(response.data.length == 0){
                        p.cancel() 
                        warn.show()
                        return
                    }
                    Promise.all([

                        mediator.api.runDps(getScript("geo"), {data: response.data})
                        .then( response => {
                            if(response.type != "error"){
                                geo.update({data:response.data})     
                                p.set({text:"get geography"}) 
                                geo.show()
                            }


                        }),

                        mediator.api.runDps(getScript("sources"), {data: response.data})
                        .then( response => {
                            sources.update({data: response.data})
                            p.set({text:"analyze sources"})
                        }),

                        mediator.api.runDps(getScript("pulse"), {data: response.data})
                        .then( response => {
                            pulse.update({data:response.data})  
                            p.set({text:"analyze pulse of news"})
                        }),

                        mediator.api.runDps(getScript("html"), {data: response.data})
                        .then( response => {
                            html.update({data: response.data})
                            p.set({text:"format news feed"})
                        }),

                    ]).then(() => {
                        p.set("dashboard completed")
                        setTimeout(()=>{
                            p.cancel() 
                            html.show()
                            pulse.show()
                            sources.show()
                            title.show()
                        },500)
                    })
                })
            })    


        }
	},
	rule: () => true		
})

