<?javascript

    let calkItem = item => {
        item.positive = (item.total>0) ? item.positive / item.total : 0
        item.negative = (item.total>0) ? item.negative / item.total : 0
        item.neutral = (item.total>0) ? 1 - item.positive - item.negative : 0
        item.s_module = (item.total > 0) ? Math.sqrt(Math.pow(item.neutral,2)+Math.pow(item.positive-item.negative,2)) : 0
        item.cos = (item.total > 0 && item.s_module > 0) ? (item.positive - item.negative) / item.s_module : 0
        item.degrees = Number.parseInt((Math.acos(item.cos) * 180  / Math.PI).toFixed(0))
        return item
    }

