data.script:
let mediator = this;
let scripts = mediator.api.selectWidgets("39g69vpifop").$refs.instance.scripts
let getScript = name => _.find(scripts,item => item.name == name).script


let keywordsWidget = mediator.api.selectWidgets("vx7xl8jks5o")
let activityChart = mediator.api.selectWidgets("sewtz8dtxxp")
let activityChart_integral = mediator.api.selectWidgets("jle9vpcfpej")
let rateChart = mediator.api.selectWidgets("495f2u6yt8b")
let rateChart_integral = mediator.api.selectWidgets("97angehg6o")
let emoChart = mediator.api.selectWidgets("u80vw6u9uac")
let vectorChart = mediator.api.selectWidgets("04tl9gt0gb2k")
let svectorChart = mediator.api.selectWidgets("varbh5c1619")

let gauge = mediator.api.selectWidgets("mu73qm7jnr")
let module = mediator.api.selectWidgets("mtez1x94va")
let time = mediator.api.selectWidgets("6v8y22uq3po")
let sa = mediator.api.selectWidgets("mqjudxkg6pk")
let events = mediator.api.selectWidgets("44q0jcqmpp6")
let conflictChart = mediator.api.selectWidgets("6u6ztbhmcs5")
let reformChart = mediator.api.selectWidgets("12epuxviya1n")
let conflictGauge = mediator.api.selectWidgets("piph1h45ukq")
let words = mediator.api.selectWidgets("rduwae51wd9")

let widgets = [
    activityChart,
    activityChart_integral,
    rateChart,
    rateChart_integral,
    emoChart,
    vectorChart,
    svectorChart,
    gauge,
    module,
    time,
    events,
    conflictChart,
    reformChart,
    conflictGauge,
    sa,
    words

]

let titles = [
    mediator.api.selectWidgets("51q057xcsid"),
    mediator.api.selectWidgets("d1hj7s9x0ut"),
    mediator.api.selectWidgets("ag6yixpio5s"),
    mediator.api.selectWidgets("x3zi3i0d4xc"),
    mediator.api.selectWidgets("al81xfer7be"),
    mediator.api.selectWidgets("j3yqdyozt3i")

]

let warning = mediator.api.selectWidgets("jeakkplfrb")
let help = mediator.api.selectWidgets("y2oizw83o2i")
let info = mediator.api.selectWidgets("dczjjylvm9c") 


widgets.forEach( w => {w.hide()})
titles.forEach( t => {t.hide()})
info.hide()
warning.show()
help.show()

this.on({
	event: "keywords-select", 
	callback: (emitter, keywords) => {
        let query = keywords.map( k => `(${k.trim()})`).join(' | ')

        if( keywords.length > 0) { 

            warning.hide()
            help.hide()
            info.show()
            widgets.forEach( w => {w.hide()})
            titles.forEach( t => {t.hide()})


            mediator.api.runDps(getScript("GET_DATA"),{query})
                    .then( response => {
                        console.log(response)
                        Promise.all(
                        [    
                            mediator.api.runDps(getScript("GET_ACTIVITY_CHART"),{data:response.data})
                                .then( response => {
                                    activityChart.show()
                                    activityChart.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_I_ACTIVITY_CHART"),{data:response.data})
                                .then( response => {
                                    activityChart_integral.show()
                                    activityChart_integral.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_RATE_CHART"),{data:response.data})
                                .then( response => {
                                    rateChart.show()
                                    rateChart.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_I_RATE_CHART"),{data:response.data})
                                .then( response => {
                                    rateChart_integral.show()
                                    rateChart_integral.update(response)
                                }), 
                            mediator.api.runDps(getScript("GET_EMO_CHART"),{data:response.data})
                                .then( response => {
                                    emoChart.show()
                                    emoChart.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_CONFLICT_CHART"),{data:response.data})
                                .then( response => {
                                    conflictChart.show()
                                    conflictChart.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_REFORM_CHART"),{data:response.data})
                                .then( response => {
                                    reformChart.show()
                                    reformChart.update(response)
                                }),    
                            mediator.api.runDps(getScript("GET_VECTORS"),{data:response.data, width:200})
                                .then( response => {
                                    vectorChart.show()
                                    vectorChart.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_S_VECTOR"),{data:response.data, width:200})
                                .then( response => {
                                    svectorChart.show()
                                    svectorChart.update(response)
                                }),    
                            mediator.api.runDps(getScript("GET_GAUGE"),{data:response.data})
                                .then( response => {
                                    gauge.show()
                                    gauge.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_MODULE"),{data:response.data})
                                .then( response => {
                                    module.show()
                                    module.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_CONFLICT_GAUGE"),{data:response.data})
                                .then( response => {
                                    conflictGauge.show()
                                    conflictGauge.update(response)
                                }),    
                            mediator.api.runDps(getScript("GET_TIME"),{data:response.data})
                                .then( response => {
                                    time.show()
                                    time.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_SA_HTML"),{data:response.data})
                                .then( response => {
                                    sa.show()
                                    sa.update(response)
                                }),    
                            mediator.api.runDps(getScript("GET_EVENTS"),{data:response.data})
                                .then( response => {
                                    events.show()
                                    events.update(response)
                                }),
                            mediator.api.runDps(getScript("GET_WORDS"),{query})
                                .then( response => {
                                    console.log(response)
                                    words.show()
                                    words.update(response)
                                }),    

                        ]).then(() => {
                            titles.forEach( t => {t.show()})
                            info.hide()
                        })        
                    })
        } else {
            widgets.forEach( w => {w.hide()})
            titles.forEach( t => {t.hide()})
            info.hide()
            warning.show()
            help.show()
        }        
	}
})

