<?javascript

    let hist = (data, bins, minValue, maxValue) => {

      minValue = minValue || _.min(data);
      maxValue = maxValue || _.max(data);
      bins = bins || 10;

      let res = []

      let delta = (maxValue - minValue)/bins

      for(let i=0; i<bins; i++) res.push({
        range:[
          i*delta+minValue,
          (i+1)*delta+minValue
        ],
        value:0
      })


      data.forEach(function(item){
        if(item != null && !Number.isNaN(item)){
          let index = Math.floor((item - minValue) / (maxValue - minValue) * bins);
          index = (index == bins) ? index - 1 : index;
          res[index].value++
        }
      })

      res = res.map( item => {
        item.value = item.value / data.length
        return item
      })
      return res;
    }

    let getHist = (responses, bins) => bins.map( t => ({
            t,
            v: responses.filter( v=> v==t).length/responses.length 
        }))

    let getHist1 = (weights,responses, bins) => bins.map( t => ({
            t,
            v: weights[_.findIndex(responses, r=> r==t)] || 0 
        }))

    let getExpertAssesments = responses => {

        let round = (value, step) => {
            step = step || (step = 1.0);
            var inv = 1.0 / step;
            return Math.round(value * inv) / inv;
        }

        let norm = data => data.map(d => (d-1)/6)

        let calcSum  = data => _.reduce(data, (d,s) => d+s, 0)

        let calcAvg = data => calcSum(data)/ data.length


        let calcDiscr = (data, base, distance) => data.map( d => (distance || (d => d))((base || calcAvg(data))-d))

        let calcExpertWeight = data => {
            let w = calcDiscr(data, calcAvg(data), Math.abs).map(d => (1-d)/ data.length)
            let s = calcSum(w)
            return w.map( d => d/s)
        }

        let calcWsum = (d,w) => calcSum( d.map( (v,index) => v*w[index]))

        let calcConcordance = (data, base, scale) => {
            let t = (scale.max-scale.min)*data.length/2
            return 1 - calcSum(calcDiscr(data, base, Math.abs).map(v => v/t))
        } 

        let w = calcExpertWeight(norm(responses))

        return {
            weights:w.map(v => Number.parseFloat(v.toFixed(4))),
            whist: getHist1(w,responses,[1,2,3,4,5,6,7]),
            value:calcWsum(responses,w),
            rvalue: round(calcWsum(responses,w), 0.5),
            avg: calcAvg(responses),
            ravg: round(calcAvg(responses), 0.5),
            concordance: calcConcordance(responses,calcWsum(responses,w),{min:1,max:7})
        }
    }

    $scope.form = $scope.form[0]

    $scope.questions = $scope.form.config.questions

    $scope.res = $scope.questions.map( q => ({
        id: q.id,
        title: `${q.options.title} (${q.options.note})`,
        responses: _.flattenDeep($scope.responses.map( r => r.data.filter(d => d.id == q.id ).map(d => d.data[0])))
    }))

    $scope.res = $scope.res.map( r => {
        r.hist = getHist(r.responses, [1,2,3,4,5,6,7])
        r.expertAssesments = getExpertAssesments(r.responses)
        return r
    })

?>    

return("res")

