/**
 **
 **     DATA PROCESSING SCRIPT: get data from sdi threats dataset
 **     SERVICE: http://localhost:8098
 **
 **/
<?javascript
    $scope.collectionMapper = d => d.value;
    $scope.dataOf2015 = d => d.value.year == "2015";
    $scope.mapGini = d => ({
        "3_alpha_code": d.left["3_alpha_code"],
        country: d.left.short_name,
        gini: d.right.value
    });
    $scope.mapSf = d => ({
        // "3_alpha_code": d.left["3_alpha_code"],
        country: d.left.country,
        gini_2015: d.left.gini,
        sf_2015: d.right.value
    })

    $scope.toFloat = d => {
        d.gini_2015 = Number.parseFloat(d.gini_2015)
        d.sf_2015 = Number.parseFloat(d.sf_2015)
        return d
    }

    $scope.toFixed3 = d => {
        d.gini_2015 = Number.parseFloat(d.gini_2015.toFixed(3))
        d.sf_2015 = Number.parseFloat(d.sf_2015.toFixed(3))
        return d
    }
?>

