c.map({{c_map}})
set("countries")

<?javascript

    $scope.res = {

        indicator: _.values($scope.indicators).map( d => ({
            text: _.trunc(d),
            max: 1,
            color: "#000000"
        })),

        series: [
            {
                type: 'radar',
                tooltip: {
                    trigger: 'item'
                },

                itemStyle: {},
                label:{
                  show:true,
                  formatter:"{c}",
                  fontSize:10,
                  fontWeight:"bold"
                },

                data: $scope.countries.map( c => {

                    let f = _.find($scope.data, d => d["3_alpha_code"] == c.selector)

                    return {
                        name: _.trunc(c.name),
                        selector:c.selector,
                        areaStyle:{
                            normal: {
                                opacity:0.1
                            }
                        },
                        value: Object.keys($scope.indicators).map(d => (1-f[d]).toFixed(2)) //f[d])//(1-f[d]).toFixed(2))
                    }        
                })
            }
        ]
    };    

?>

return ("res")
